// Calculate date range for last week
const now = new Date();
const lastMonday = new Date(now);
lastMonday.setDate(now.getDate() - now.getDay() - 6); // Previous Monday
lastMonday.setHours(0, 0, 0, 0);

const lastSunday = new Date(lastMonday);
lastSunday.setDate(lastMonday.getDate() + 6); // Previous Sunday
lastSunday.setHours(23, 59, 59, 999);

const startDate = lastMonday.toISOString().split('T')[0];
const endDate = lastSunday.toISOString().split('T')[0];
const period = `${startDate}_to_${endDate}`;

return [{
  json: {
    start_date: startDate,
    end_date: endDate,
    period: period,
    week_number: getWeekNumber(lastMonday),
    year: lastMonday.getFullYear()
  }
}];

// Helper function to get ISO week number
function getWeekNumber(date) {
  const d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
  const dayNum = d.getUTCDay() || 7;
  d.setUTCDate(d.getUTCDate() + 4 - dayNum);
  const yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
  return Math.ceil((((d - yearStart) / 86400000) + 1) / 7);
}

// Format summary data for AI analysis
const item = $input.first();
const summary = item.json;

// Calculate key metrics
const totalSpent = summary.totals_by_category
  .filter(c => c.category !== 'Income' && c.category !== 'Savings')
  .reduce((sum, c) => sum + c.amount_cents, 0);

const totalIncome = summary.totals_by_category
  .filter(c => c.category === 'Income')
  .reduce((sum, c) => sum + c.amount_cents, 0);

const topCategory = summary.totals_by_category
  .filter(c => c.category !== 'Income' && c.category !== 'Savings')
  .sort((a, b) => b.amount_cents - a.amount_cents)[0];

const dailyAverage = summary.timeseries
  .reduce((sum, day) => sum + day.sum_cents, 0) / summary.timeseries.length;

// Format for AI prompt
const formattedData = {
  period: summary.period,
  total_spent_usd: (totalSpent / 100).toFixed(2),
  total_income_usd: (totalIncome / 100).toFixed(2),
  net_savings_usd: ((totalIncome - totalSpent) / 100).toFixed(2),
  daily_average_usd: (dailyAverage / 100).toFixed(2),
  top_category: topCategory?.category || 'N/A',
  top_category_amount_usd: ((topCategory?.amount_cents || 0) / 100).toFixed(2),
  top_vendors: summary.top_vendors.slice(0, 5).map(v => ({
    vendor: v.vendor,
    amount_usd: (v.amount_cents / 100).toFixed(2)
  })),
  category_breakdown: summary.totals_by_category
    .filter(c => c.category !== 'Income' && c.category !== 'Savings')
    .map(c => ({
      category: c.category,
      amount_usd: (c.amount_cents / 100).toFixed(2),
      percentage: ((c.amount_cents / totalSpent) * 100).toFixed(1)
    }))
};

return [{
  json: {
    raw_summary: summary,
    formatted_data: formattedData,
    period: summary.period
  }
}];

