// Log workflow completion
const item = $input.first();

console.log('=== Weekly Report Generated ===');
console.log(`Period: ${item.json.period}`);
console.log(`Total Spent: $${item.json.metrics.total_spent_usd}`);
console.log(`Net Savings: $${item.json.metrics.net_savings_usd}`);
console.log(`Top Category: ${item.json.metrics.top_category}`);
console.log('Report stored successfully');

return [item];

// Error handler for weekly report workflow
const error = $input.first();

console.error('=== Weekly Report Workflow Error ===');
console.error('Error:', error.error);
console.error('Node:', error.node);
console.error('Details:', JSON.stringify(error.json, null, 2));

// Optionally send error notification via Telegram
if (process.env.TELEGRAM_CHAT_ID) {
  return [{
    json: {
      error: true,
      message: `‚ùå Weekly report failed: ${error.error?.message || 'Unknown error'}`,
      timestamp: new Date().toISOString(),
      send_telegram: true
    }
  }];
}

return [{
  json: {
    error: true,
    message: error.error?.message || 'Unknown error',
    timestamp: new Date().toISOString()
  }
}];
